import {useParams} from "react-router-dom";
import './WriteReviewCss.css';
import {useState, useEffect} from "react";
import {useDropzone} from 'react-dropzone';


const WriteReview = () => {

    const welfareName = useParams().name;
    const username = useParams().username;
    const [uploadedFiles, setUploadedFiles] = useState([]);
    const onDrop = (acceptedFiles) => {
      setUploadedFiles((prevFiles) => [...prevFiles, ...acceptedFiles]);
    };

    const { getRootProps, getInputProps } = useDropzone({ onDrop });

    const submitControl = async (e) => {
        e.preventDefault();
    
        

        const newNotename = document.getElementById('newNotename').value;
        const newNotepass = document.getElementById('newNotepass').value;
        const newNote = document.getElementById('newNote').value;
    
        try {
          const response = await fetch('http://localhost:8080/main/createReview', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              username: username,
              notename: newNotename,
              image: null,
              sw_name: welfareName,
              notepass: newNotepass,
              note: newNote,
            }),
          });
    
          if (response.ok) {
            console.log("입력성공")
            console.log(response)
          } else {
            console.log("입력실패. 서버오류발생1")
          }
        } catch (error) {
            console.log("입력실패. 서버오류발생2")
        }
      };

      const dropzoneStyle = {
        border: '2px dashed #ccc',
        borderRadius: '4px',
        padding: '20px',
        textAlign: 'center',
        cursor: 'pointer',
      };
      
      const imageStyle = {
        width: '200px',
        height: '200px',
        objectFit: 'cover',
        margin: '10px',
      };
      

    
    return(
        <>
        <div className="HelloWorld">
            <div>제목:<input type="text" id="newNotename"></input></div>
            <div>작성자:{username}</div>
            <div>요양원명:{welfareName}</div>
            <div>비밀번호:<input type="password" id="newNotepass"></input></div>
            <div>내용:<input type="text" id="newNote"></input></div>
            <button type="button" onClick={submitControl}>작성</button>
        </div>
        <div>
          <div {...getRootProps()} style={dropzoneStyle}>
            <input {...getInputProps()} />
            <p>이미지를 업로드하려면 클릭하거나 파일을 끌어다 놓으세요.</p>
          </div>
          <div>
            {uploadedFiles.map((file, index) => (
              <div key={index}>
              <img src={URL.createObjectURL(file)} alt={`Uploaded ${index}`} style={imageStyle} />
            </div>
            ))}
          </div>
        </div>
        
        
        </>
        
    )

}

export default WriteReview;
