// loginpage.js

import React, { useState } from 'react';
import loginpageC from './loginpage.module.css';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // 간단한 로그인 체크 (예시이므로 실제 서버 인증은 필요하지 않음)
    if (username === 'user' && password === 'password') {
      setMessage('로그인 성공!');
    } else {
      setMessage('로그인 실패. 올바른 정보를 입력하세요.');
    }
  };

  return (
    <div className={loginpageC.container}>
      <h2>로그인</h2>
      <form onSubmit={handleSubmit} className={loginpageC.form}>
        <label htmlFor="username" className={loginpageC.label}>
          사용자 이름:
        </label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
          className={loginpageC.input}
        />
        <label htmlFor="password" className={loginpageC.label}>
          비밀번호:
        </label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          className={loginpageC.input}
        />
        <button type="submit" className={loginpageC.button}>
          로그인
        </button>
      </form>
      <p className={loginpageC.message}>{message}</p>
    </div>
  );
};

export default LoginForm;
