import React, { useState } from 'react';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [showSignup, setShowSignup] = useState(false); // State to control the visibility of the signup form

  const handleLoginSubmit = (e) => {
    e.preventDefault();

    // Simple login check (example, not real authentication)
    if (username === 'user' && password === 'password') {
      setMessage('로그인 성공!');
    } else {
      setMessage('로그인 실패. 올바른 정보를 입력하세요.');
    }
  };

  const handleSignupSubmit = (e) => {
    e.preventDefault();
    // Perform signup logic here (e.g., sending data to the server)

    // For this example, let's assume signup is successful
    setMessage('회원가입 성공!');
  };

  const handleSignupButtonClick = () => {
    setShowSignup(true); // Show the signup form when "회원가입" button is clicked
  };

  return (
    <div className='loginpage'>
      <form onSubmit={handleLoginSubmit}>
        <label htmlFor="username"><h3>사용자 이름</h3></label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label htmlFor="password"><h3>비밀번호</h3></label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" className="login-button">로그인</button>
        <button type="button" className="signup-button" onClick={handleSignupButtonClick}>
          회원가입
        </button>
      </form>
      <p>{message}</p>

      {showSignup && ( // Conditionally render the signup form
        <form onSubmit={handleSignupSubmit}>
          <h3>회원가입</h3>
          <label htmlFor="newUsername">새 사용자 이름</label>
          <input
            type="text"
            id="newUsername"
            required
          />
          <label htmlFor="newPassword">새 비밀번호</label>
          <input
            type="password"
            id="newPassword"
            required
          />
          <button type="submit" className="signup-button">회원가입</button>
        </form>
      )}
    </div>
  );
};

export default LoginForm;
