import React, { useEffect, useState } from 'react';
import './InfoModalCss.css';

const InfoModal = (props) => {

    
    const [message, setMessage] = useState('');
    var name = props.name

    const sendName = async (e) => {
        //e.preventDefault();
    
        try {
          const response = await fetch('http://10.125.121.202/api/searchName', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              name
            }),
          });
    
          if (response.ok) {
            setMessage(`${name} 전송 성공`);
            console.log(message)
            
          } else {
            setMessage('전송 실패.');
            console.log(message)
          }
        } catch (error) {
          setMessage('서버 오류 발생.');
          console.log(message)
        }
      };


useEffect(() => {

    sendName()

}, [])
return (
    <>
    <div className="infoModal">
    <h1 className="name">{props.name}</h1>
    <h1>{message}</h1>
    </div>
    
    
    </>
);

}

export default InfoModal;
