import React, { useState } from 'react';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // 간단한 로그인 체크 (예시이므로 실제 서버 인증은 필요하지 않음)
    if (username === 'user' && password === 'password') {
      setMessage('로그인 성공!');
    } else {
      setMessage('로그인 실패. 올바른 정보를 입력하세요.');
    }
  };

  const newlog =() =>{
    
  }

  return (
    <div className='loginpage'>
      <form onSubmit={handleSubmit}>
        <label htmlFor="username"><h3>사용자 이름</h3></label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label htmlFor="password"><h3>비밀번호</h3></label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" className="login-button">로그인</button>
        <button type="submit" className="signup-button" onclick ={newlog}>회원가입</button>
      </form>
      <p>{message}</p>
    </div>
  );
};

export default LoginForm;
