import React, { useState } from 'react';
//import style from './loginpage.module.css'

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [showSignUp, setShowSignUp] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    // 간단한 로그인 체크 (예시이므로 실제 서버 인증은 필요하지 않음)
    if (username === 'user' && password === 'password') {
      setMessage('로그인 성공!');
    } else {
      setMessage('로그인 실패. 올바른 정보를 입력하세요.');
    }
  };

  const handleSignUp = (e) => {
    e.preventDefault();
    // Implement your sign-up logic here
    // For example, you could show a modal or navigate to a sign-up page
    // For this example, we'll just display a message.
    setMessage('회원가입 버튼을 눌렀습니다.');
  };

  return (
    <div className='loginpage'>
      <h5>로그인</h5>
      <form onSubmit={handleSubmit}>
        <label htmlFor="username">사용자 이름:</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label htmlFor="password">비밀번호:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">로그인</button>
        <button type="button" onClick={() => setShowSignUp(true)}>회원가입</button>
      </form>
      <p>{message}</p>

      {/* Render the sign-up form in a dropdown or modal */}
      {showSignUp && (
        <div className="signup-form">
          {/* Implement your sign-up form content here */}
          {/* For this example, we'll just show a message */}
          <h5>회원가입</h5>
          <p>회원가입 양식을 작성하세요.</p>
          <button onClick={() => setShowSignUp(false)}>닫기</button>
        </div>
      )}
    </div>
  );
};

export default LoginForm;
