import { useParams } from "react-router-dom";
import './WriteReviewCss.css';
import React, { useState } from "react";

const WriteReview = () => {
    const welfareName = useParams().name;
    const username = useParams().username;

    const [notename, setNotename] = useState('');
    const [notepass, setNotepass] = useState('');
    const [note, setNote] = useState('');
    const [uploadedFile, setUploadedFile] = useState(null);

    const handleFileChange = (event) => {
        const selectedFile = event.target.files[0];
        setUploadedFile(selectedFile);
    };

    const submitControl = async (e) => {
        e.preventDefault();

        // const formData = new FormData();
        // formData.append('username', username);
        // formData.append('notename', notename);
        // formData.append('sw_name', welfareName);
        // formData.append('notepass', notepass);
        // formData.append('note', note);


        // const imageFormData = new FormData();
        // imageFormData.append('image', uploadedFile);
        const formData = new FormData();
        //formData.append('key1', 'value1'); // 여기에 실제로 사용하는 폼 필드를 추가하세요

        formData.append('username', username);
        formData.append('notename', notename);
        formData.append('sw_name', welfareName);
        formData.append('notepass', notepass);
        formData.append('note', note);
        const imageFormData = new FormData();
        imageFormData.append('imageKey', imageFile); // imageFile 자리에 실제 이미지 파일을 넣어주세요

        const boundary = '----WebKitFormBoundary' + new Date().getTime(); // 고유한 경계 값을 생성합니다

        const combinedFormData = new FormData();
        combinedFormData.append('key1', 'value1');

        // 이전 formData의 파트를 추가합니다
        for (const [key, value] of formData.entries()) {
            combinedFormData.append(key, value);
        }

        // 이미지 파트를 추가합니다
        combinedFormData.append('imageKey', imageFile);

        try {
            const response = await fetch('http://localhost:8080/main/createReview', {
                method: 'POST',
                headers: {
                    'Content-Type': 'multipart/form-data'
                },
                body: {
                    formData, imageFormData
                }


            });

            if (response.ok) {
                console.log("입력성공");
                console.log(response);
                window.close();
            } else {
                console.log("입력실패. 서버오류발생1");
            }
        } catch (error) {
            console.log("입력실패. 서버오류발생2");
        }
    };

    return (
        <>
            <div className="WriteReviewWindow">
                <div>제목:&nbsp;<input type="text" value={notename} onChange={(e) => setNotename(e.target.value)} /></div>
                <div>작성자:&nbsp;{username}</div>
                <div>요양원명:&nbsp;{welfareName}</div>
                <div>비밀번호:&nbsp;<input type="password"
                    id="newPassword" value={notepass} placeholder="비밀번호를 반드시 입력하세요" onChange={(e) => setNotepass(e.target.value)} required /></div>
                <br />
                <div>내용:&nbsp;<input type="text" id="newNote" value={note} onChange={(e) => setNote(e.target.value)} /></div>
                <br />
                <input type="file" onChange={handleFileChange} />
                <button type="submit" onClick={submitControl} id="submitButton">작성</button>
            </div>
        </>
    );
};

export default WriteReview;
