import { BrowserRouter, Routes, Route } from "react-router-dom";
import React, { useState } from 'react';
import {RecoilRoot} from 'recoil';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import KakaoMap from './KakaoMap';
import Main from './Main';
import Loginpage from './loginpage';
import InfoModal from './InfoModal'
import WriteReview from './WriteReview';
import {UserInfoAtoms} from './Atoms/UserInfoAtoms'
import { useRecoilState, useRecoilValue } from "recoil";


import './MainFrameCss.css';




const MainFrame = () => {


    const [searchText, setSearchText] = useState('');
    const username = useRecoilValue(UserInfoAtoms)    
    const handleSearchChange = (e) => {
      setSearchText(e.target.value);
    };
  
    return (
    
      <RecoilRoot>
      
      <main className="mainBody">
        <header>
          <div className="App">
            <AppBar position="static" className="appbar">
              <Toolbar style={{ backgroundColor: "#74A629" }} className="toolbar">

                <h1>노인을 위한 나라 </h1>&nbsp;
                <div className="loginpage"><Loginpage /></div>
                {username!=""?<div>{username}님 어서오세요!</div>:<div>로그인해주세요</div>}
              </Toolbar>           
            </AppBar>
          </div>
           
        </header>
        
        <div>
          <Main />
        </div>
        <div className="App1">
          
  
          <KakaoMap/>
        </div>
        <footer>
          <div>
          ☘ made by Suyeon Lee, Soyun Yang ☘
          </div>
        </footer>
      </main>
      </RecoilRoot>
   
    );

}

export default MainFrame;
