import React, { useEffect, useState } from 'react';
import './InfoModalCss.css';
import BoardOfWelfare from './BoardOfWelfare';
import {LoginAtoms} from './LoginAtoms';
import {useRecoilValue} from 'recoil';

const InfoModal = (props) => {

    
    const [message, setMessage] = useState('');
    const loginState = useRecoilValue(LoginAtoms);
    var name = props.name

    const sendName = async (e) => {
        //e.preventDefault();
    
        try {
          const response = await fetch('http://10.125.121.202/api/searchName', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              name
            }),
          });
    
          if (response.ok) {
            setMessage(`${name} 전송 성공`);
            console.log(message)
            
          } else {
            setMessage('전송 실패.');
            console.log(message)
          }
        } catch (error) {
          setMessage('서버 오류 발생.');
          console.log(message)
        }
      };


useEffect(() => {

    sendName()

}, [])

useEffect(() => {
  console.log("loginstate:",loginState)
}, [loginState])
return (
    <>
    <div className="infoModal">
      &nbsp;&nbsp;
      <div className="upperSideOfModal">
        <div className="nameOfWelfare">{props.name}</div>
        &nbsp;
        <div className="infoOfWelfare">{message}</div>
        {loginState == 1 ?
        (<button className="writeReviewButton">리뷰 작성</button>):
        (<div className="loginMessage">로그인을 하셔야 리뷰 작성이 가능합니다</div>) 
        }
      </div>
      <BoardOfWelfare />
    </div>
    
    </>
);

}

export default InfoModal;
