import {useParams} from "react-router-dom";
import './WriteReviewCss.css';


const WriteReview = () => {

    const submitControl = async (e) => {
        e.preventDefault();
    
        const welfarename = useParams().name;

        const newNotename = document.getElementById('newNotename').value;
        const newNotepass = document.getElementById('newNotepass').value;
        const newNote = document.getElementById('newNote').value;
    
        try {
          const response = await fetch('http://localhost:8080/api/signup', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              sw_name: welfarename,
              note_pass: newNotepass,
              Note: newNote,
              notename: newNotename,
            }),
          });
    
          if (response.ok) {
            setMessage('회원가입 성공!');
            setShowSignupModal(false); // Close the signup modal after successful signup
          } else {
            setMessage('회원가입 실패. 서버 오류가 발생했습니다.');
          }
        } catch (error) {
          setMessage('회원가입 실패. 서버 오류가 발생했습니다.');
        }
      };

    
    return(
        <>
        <div className="HelloWorld">
            <div>제목:<input type="text" htmlFor="newNotename"></input></div>
            <div>작성자:</div>
            <div>요양원명:{welfarename}</div>
            <div>비밀번호:<input type="password" htmlFor="newNotepass"></input></div>
            <div>내용:<input type="text" htmlFor="newNote"></input></div>
            <button type="button" onClick={submitControl}>작성</button>
        </div>
        
        
        </>
        
    )

}

export default WriteReview;
